import React from 'react';
import '../styles/App.css';
import LeafletMap from './LeafletMap';
import RouterPanel from './RouterPanel';
import SimulationOptions from './SimulationOptions';
import {MDBIcon} from 'mdbreact';
import routers from "../routersList";

const App = () => {

  const [showSimulationOptions, setShowSimulationOptions] = React.useState(false);
  const [simulationsButton, setSimulationButton] = React.useState('amber-text')

  const toogleSimulationOptions = () => {
    setShowSimulationOptions(!showSimulationOptions)
    if(showSimulationOptions){
      setSimulationButton('stylish-color-text');
    }
    else{
      setSimulationButton('amber-text');
    }
  }

  return (
    <div className="App">
      <header className="App-header">
          <MDBIcon icon = 'wifi' size="4x" className="amber-text" onClick />
          <MDBIcon icon = 'bars' size="4x" className="amber-text pr-3" />
      </header>
      <div className="body">
        <RouterPanel style={{width:'400px'}} routers={routers}/>
       <LeafletMap/>
       <SimulationOptions show={showSimulationOptions}/>
      </div>
    </div>
  );
}

export default App;
