import React from 'react';
import '../styles/App.css';
import LeafletMap from './LeafletMap';
import RouterPanel from './RouterPanel';
import SimulationOptions from './SimulationOptions';
import {MDBIcon} from 'mdbreact';
import routers from "../routersList";

const App = () => {

  const initialPosition = {
  'lat': 43.5385, 
  'lng': -79.6658873,
  'direction' : 0, //   0->east, 1->north, 2->weast, 3->south
  'step' : 15,
  }


  const stepCounter = () => {
    let stepCounter = patrol.step;
    let patrolDirection = patrol.direction;
    if(stepCounter > 0){
      stepCounter = stepCounter - 1;
      return {'step': stepCounter, 'direction': patrolDirection};
    }
    else{
      patrolDirection = patrolDirection + 1;
      stepCounter = 15;
      return {'step': stepCounter, 'direction': patrolDirection};
    }
  }

  const [showSimulationOptions, setShowSimulationOptions] = React.useState(true);
  const [patrol, setPatrol] = React.useState(initialPosition);


  const toogleSimulationOptions = () => {
    setShowSimulationOptions(!showSimulationOptions)
  }

  const buttonColor = () => {
    return showSimulationOptions ? 'amber-text' : 'grey-text';
  }

  const display = () => {
    return showSimulationOptions ? 'Show' : 'Hide';
}

  const updatePatrolPosition = () => {
    let newPatrol = {};
    switch (patrol.direction%4) {
      case 0:
        newPatrol.lat = patrol.lat;
        newPatrol.lng = patrol.lng - 0.0001;
        break;
      case 1:
        newPatrol.lat = patrol.lat + 0.0001;
        newPatrol.lng = patrol.lng;
        break;
      case 2:
        newPatrol.lat = patrol.lat;
        newPatrol.lng = patrol.lng + 0.0001;
        break;
      case 3:
        newPatrol.lat = patrol.lat - 0.0001;
        newPatrol.lng = patrol.lng;
        break;
    }
    newPatrol = {...newPatrol, ...stepCounter()};
    setPatrol(newPatrol);
  }

  setTimeout(updatePatrolPosition, 1000);

  return (
    <div className="App">
      <header className="App-header">
          <MDBIcon icon = 'wifi' size="4x" className='amber-text'/>
          <MDBIcon icon = 'bars' size="4x" className={buttonColor()} onClick={()=>toogleSimulationOptions()}  />
      </header>
      <div className="body">
        <RouterPanel className='RouterPanel' routers={routers}/>
        <LeafletMap routers={routers} patrol={patrol}/>
        <SimulationOptions className={display()}/>
      </div>
    </div>
  );
}

export default App;
